import  React, { Component } from  'react';
import { BrowserRouter as Router, Route} from  'react-router-dom';
import {Button, Row, Col, Layout, Menu, Breadcrumb, Icon, Switch, Dropdown} from 'antd';
import 'antd/dist/antd.css';
import Typist from 'react-typist';
import * as Scroll from 'react-scroll';
import { Link, Element , Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'

import MouseTrail from './MouseTrail';
import  DescribeMe  from  './DescribeMe';
import  WhatIDo  from  './WhatIDo';
import  PageFooter from './PageFooter';
import  NavBar  from  './NavBar';
import  Fade from 'react-reveal/Fade';
import styled from 'styled-components';

const { Header, Content, Footer, Sider }  = Layout;

class  Landing  extends  Component {

  constructor(props) {
    super(props);
    this.state  = {
      resizeNotifier: () => {},
      width:          window.innerWidth,
      lastScrollY:    window.scrollY,
      xMain:         0,
      yMain:         0,
      xTrailing:     0,
      yTrailing:     0,

      introBackground: true,
      startText:      false,
      endText:        false,
      startAnimation: true,
      showSite:       false,
      showSite:       false,
      loadMessage: "Time for a run",
    }
  }

  componentDidMount() {
    document.title    = "I'm Kyle Christ";
    document.themeColor =  "#b09a40";
    window.addEventListener("resize", this.resize.bind(this));
    window.addEventListener('scroll', this.handleScroll.bind(this), true);
    this.resize();
    this.isShow();
    this.keyFrame1();
    this.keyFrame2();
    this.keyFrame3();
    this.keyFrame4();
    const chooseMessage = ["Time for a run", "Your patience is appreciated", "Loading the pixels on your screen"];
    const chooseNumber = Math.floor(Math.random(2)*3);
    console.log("NUMBER: "+chooseNumber);
    this.setState({loadMessage:    chooseMessage[chooseNumber]});
  }

  resize = () => {
      this.setState({width:    window.innerWidth});
  }

  handleScroll = () => {
    this.setState({lastScrollY:    window.scrollY});
  }

  handleMouseMove = (e) => {
    // Using pageX and pageY will cause glitching when you scroll the window down
    // because it measures the distance from the top left rendered corner, not
    // top left visible corner
    const { clientX, clientY } = e;

    // we set the main circle coordinates as soon as the mouse is moved
    this.setState({
      xMain: clientX,
      yMain: clientY,
    }, () => {
      // this callback is invoked after the first setState finishes
      //
      // here we schedule saving the trailing coordinates in state 100ms
      // after the main coordinates have been set to simulate the trailing
      setTimeout(() => {
        this.setState({
          xTrailing: clientX,
          yTrailing: clientY,
        })
      }, 100);
    })
  }

  _onMouseMove = (e) => {
       const width = this.refs.titleContainer.clientWidth;
       const height = this.refs.titleContainer.clientHeight;
       const oX = (e.nativeEvent.offsetX/width) * 100;
       const oY = (e.nativeEvent.offsetY/height) * 100;
       console.log(Math.floor(oX), Math.floor(oY));
       this.setState({
           xMain: oX,
           yMain: oY
       });
   }


  // Timer for intro text animation
  keyFrame1() {
    setTimeout(() => {
      this.setState({ startText: true });
    }, 300);
  }

  // Timer for outro text animation
  keyFrame2() {
    setTimeout(() => {
      this.setState({ startText: false });
    }, 2000);
  }

  // Start animation and reveal site
  keyFrame3() {
    setTimeout(() => {
      this.setState({ startAnimation: true,
                      showSite: false});
    }, 2300);
  }

  keyFrame4() {
    setTimeout(() => {
      this.setState({ introBackground: false,
                      showSite: true});
    }, 2600);
  }

  // Return function for text
  isIntroBackground(state) {
    if (state){
      return {height:'100%', backgroundColor:'#010101', color:'#fff', position:'fixed', top:'0', right:'0', left:'0', bottom:'0', zIndex:'5'}
    } else {
      return {display:'none'}
    }
  }

  // Return function for animation
  isKeyFrame1(timeout, width) {
    if (timeout){
      const loadTextAnim = styled.h1`
      z-index:5;
      color:#fff;
      font-size:3vw;
      font-style:SuisseIntl-Light;
      letter-spacing:1.1px;
      font-seight:400;
      text-align:center;
      margin-left:auto;
      margin-right:auto;
      margin-top:25%
      animation: moveTextUp 1.5s cubic-bezier(.79,.17,.28,1.1),moveTextUpper 1.5s cubic-bezier(.79,.17,.28,1.1) 3s forwards;
      animation-duration: 1.5s, 1.5s;
      animation-timing-function: cubic-bezier(0.79, 0.17, 0.28, 1.1), cubic-bezier(0.79, 0.17, 0.28, 1.1);
      animation-delay: 0s, 3s;
      animation-iteration-count: 1, 1;
      animation-direction: normal, normal;
      animation-fill-mode: none, forwards;
      animation-play-state: running, running;
      animation-name: moveTextUp, moveTextUpper;
      `;
      if(width > 900){

        /*
        zIndex:'5',
                color:'#fff',
                fontSize:"3vw",
                fontStyle:'SuisseIntl-Light',
                letterSpacing:'1.1px',
                fontWeight:'400',
                textAlign:'center',
                marginLeft:'auto',
                marginRight:'auto',
                marginTop:'25%'
        */
        return {
          loadTextAnim
              }
        } else {
          return {loadTextAnim
                }
        }
    } else {
      return {display:'none'}
    }
  }

  // Return function for animation
  isKeyFrame2(timeout) {
    if (timeout){
      return {height:'100%', backgroundColor:'#010101',
      color:'#fff', position:'fixed', top:'0', right:'0', left:'0', bottom:'0',
      justifyContent:'center', alignItems:'center', zIndex:'999',
      animation:'wipeloader', animationDuration:'2.2s', animationDelay:'0.8s',
      animationDirection:'reverse', animationFillMode:'forwards'}
    } else {
      return {display:'none'}
    }
  }

  // Return function for text
  isShow(state) {
    if (state){
      return {}
    } else {
      return {display:'none'}
    }
  }


  render() {

    const {
      xMain,
      yMain,
      xTrailing,
      yTrailing
    } = this.state;

    var screenWidth = this.state.width;

    var loadingText;
    var leftHeaderText;
    var rightHeaderText;
    var contentWrapper;
    var leftWrapper;
    var rightWrapper;
    var chooseColor;

    if (screenWidth > 1250){
      return (
        <React.Fragment>
        <div ref="titleContainer" onMouseMove={this._onMouseMove} onMouseOut={this._onMouseOut}>
          <div style={this.isIntroBackground(this.state.introBackground)}>
            <Fade bottom opposite when={this.state.startText}>
            <h4 style={this.isKeyFrame1(this.state.startText, screenWidth)}> {this.state.loadMessage} </h4>
            </Fade>
          </div>
          <div style={this.isShow(this.state.showSite)}>
          <DescribeMe/>
          <PageFooter />
          </div>
        </div>
        </React.Fragment>
      )

    } else if (screenWidth > 900 ){
      return (
        <React.Fragment>
        <div ref="titleContainer" onMouseMove={this._onMouseMove} onMouseOut={this._onMouseOut}>
          <div style={this.isIntroBackground(this.state.introBackground)}>
            <h4 style={this.isKeyFrame1(this.state.startText)}> {this.state.loadMessage} </h4>
          </div>
          <div style={this.isShow(this.state.showSite)}>
          <DescribeMe/>
          <PageFooter />
          </div>
        </div>
        </React.Fragment>
      )
    } else {
      return (
        <React.Fragment>
          <div style={this.isIntroBackground(this.state.introBackground)}>
            <Fade bottom opposite when={this.state.startText}>
            <h4 style={this.isKeyFrame1(this.state.startText)}> {this.state.loadMessage} </h4>
            </Fade>
          </div>
          <div style={this.isShow(this.state.showSite)}>
          <DescribeMe/>
          <PageFooter />
          </div>
        </React.Fragment>
      )
    }
  }
}

export  default  Landing;
